// Phase 1: DOM Selection
const taskInput = document.getElementById('taskInput');
const addTaskButton = document.getElementById('addTask');
const taskList = document.getElementById('tasklist');
const fetchTasksButton = document.getElementById('fetchTasks');

// Phase 2: Add Task Function
function addTask() {
  const taskText = taskInput.value;
  const li = document.createElement('li');
  li.innerHTML = `${taskText} <button class='delete'>Delete</button>`;
  taskList.appendChild(li);
  taskInput.value = '';
}

// Phase 2: Add Task Event Listener
addTaskButton.addEventListener('click', addTask);

// Phase 3: Delete Task Function
function deleteTask(e) {
  if (e.target.classList.contains('delete')) {
    e.target.parentElement.remove();
  }
}

// Phase 3: Delete Task Event Listener (Event Delegation)
taskList.addEventListener('click', deleteTask);

// Phase 4: Fetch Tasks Async Function
async function fetchTasks() {
  try {
    const response = await fetch('https://jsonplaceholder.typicode.com/todos?_limit=5');
    const data = await response.json();
    data.forEach(task => {
      const li = document.createElement('li');
      li.innerHTML = `${task.title} <button class='delete'>Delete</button>`;
      taskList.appendChild(li);
    });
  } catch(error) {
    console.error('An error occurred while fetching tasks: ' + error);
  }
}

// Phase 4: Fetch Tasks Event Listener
fetchTasksButton.addEventListener('click', fetchTasks);

